#! /usr/bin/env python3

"""
Used to generate senzing/unmarshal_test.go
"""

import json
import logging
import os
import re
from datetime import datetime, timezone

INPUT_FILE = "bin/response-test-cases.json"
OUTPUT_FILE = "senzing/unmarshal_test.go"

# -----------------------------------------------------------------------------
# --- Output templates
# -----------------------------------------------------------------------------

OUTPUT_HEADER = """// DO NOT EDIT.  This code is generated.
// Generated by: g2-sdk-go/bin/generate_senzing_unmarshal_test.py
// Generated for: g2-sdk-go/senzing/unmarshal_test.go
"""

OUTPUT_HEADER += f"// Generated date: {datetime.now(timezone.utc).isoformat()}\n"

# noqa: E101
OUTPUT_HEADER += """
package senzing

import (
    "context"
    "fmt"
    "testing"

    truncator "github.com/aquilax/truncate"
    "github.com/stretchr/testify/assert"
)

const (
    defaultTruncation = 127
    printResults      = false
)

// ----------------------------------------------------------------------------
// Internal functions
// ----------------------------------------------------------------------------

func truncate(aString string, length int) string {
    return truncator.Truncate(aString, length, "...", truncator.PositionEnd)
}

func printResult(test *testing.T, title string, result interface{}) {
    if printResults {
        test.Logf("%s: %+v", title, truncate(fmt.Sprintf("%+v", result), defaultTruncation))
    }
}

func printActual(test *testing.T, actual interface{}) {
    printResult(test, "Actual", actual)
}

func testError(test *testing.T, ctx context.Context, err error) {
    _ = ctx
    if err != nil {
        test.Log("Error:", err.Error())
        assert.FailNow(test, err.Error())
    }
}

// ----------------------------------------------------------------------------
// Test interface functions
// ----------------------------------------------------------------------------
"""  # noqa: E101, W191

TEST_FUNCTION_TEMPLATE = f"""
    ctx := context.TODO()
    jsonString := `{{json}}`
    result, err := Unmarshal{{struct}}(ctx, jsonString)
    testError(test, ctx, err)
    printActual(test, result)
"""  # noqa: E101,F541,W191

OUTPUT_FOOTER = """
"""

# -----------------------------------------------------------------------------
# --- Helpers
# -----------------------------------------------------------------------------


def canonical_json(json_object):
    """Create compact JSON.  No spaces."""
    result = json.dumps(json_object, sort_keys=True, separators=(",", ":"))
    return result


def canonical_testcase_name(testcase_name):
    return re.sub("[^0-9a-zA-Z]+", "", testcase_name).capitalize()


# -----------------------------------------------------------------------------
# --- Main
# -----------------------------------------------------------------------------

# Set up logging.

logging.basicConfig(format="%(asctime)s %(message)s", level=logging.INFO)

# Prolog.

logging.info("{0}".format("-" * 80))
logging.info("--- {0} - Begin".format(os.path.basename(__file__)))
logging.info("{0}".format("-" * 80))

# Load testcase metadata.

with open(INPUT_FILE, encoding="utf-8") as input_file:
    response_test_cases = json.load(input_file)

# Write testcase file.

with open(OUTPUT_FILE, "w", encoding="utf-8") as file:
    file.write(OUTPUT_HEADER)
    for senzing_api_class, bob in response_test_cases.items():
        method_test_cases = bob.get("tests", {})
        for testcase_name, test_case_json in method_test_cases.items():
            file.write(
                "\nfunc Test{0}{1}(test *testing.T) {{".format(
                    senzing_api_class, canonical_testcase_name(testcase_name)
                )
            )
            file.write(
                TEST_FUNCTION_TEMPLATE.format(
                    json=canonical_json(test_case_json), struct=senzing_api_class
                )
            )
            file.write("}")
    file.write(OUTPUT_FOOTER)

# Epilog.

logging.info("{0}".format("-" * 80))
logging.info("--- {0} - End".format(os.path.basename(__file__)))
logging.info("{0}".format("-" * 80))
